ng: 5px 0 3px 0;\n    display: block\n}\n/* phrase */\n.phrase .grid_7 {\n    margin: 0;\n    width: 100%;\n    float: none;\n    display: block\n}\n/* hide banner text and size image area */\n.banner-text {\n    display: none;\n    width: 0\n}\n.banner-image {\n    display: block;\n    width: 100%;\n    float:none;\n    margin: 0;\n    padding: 0\n}\n/* hide print button */\na.printbutton {\n    display: none\n}\n\n/* =====================================\nTablet (Portrait) \n===================================== */\n@media only screen and (min-width: 768px) and (max-width: 1024px) and (orientation : portrait) {\n    /* realign navigation for mobile devices */\n    nav.main-navigation {\n        float: none;\n        margin: 0;\n        line-height: 1\n    }\n    nav.main-navigation ul li {\n        position: static\n    }\n    nav.main-navigation > ul > li > a,\n    nav.main-navigation > ul > li.sectionheader > span {\n        font-size: 14px\n    } \n    .header-bottom {\n        height: auto;\n        line-height: 1.2;\n        padding: 10px 0\n    }\n    .search input {\n        width: 60%;\n        text-align: center;\n        padding: 5px 0 3px 0;\n        display: block\n    }\n}\n\n/* =====================================\nSmartphones (Landscape) \n===================================== */\n@media only screen and (min-width: 321px) and (max-width: 767px) { \n    nav.main-navigation ul li {\n        position: static\n    }    \n    nav.main-navigation > ul > li > a.\n    nav.main-navigation > ul > li.sectionheader > span {\n        font-size: 10px\n    }\n}\n\n/* =====================================\nSmartphones (Portait) \n===================================== */\n@media only screen and (max-width: 320px) {\n    .logo img {\n        margin: 20px auto\n    }\n    /* realing navigation */\n    nav.main-navigation {\n        line-height: 1\n    }\n    nav.main-navigation > ul > li:hover > ul {\n        top: 58px;\n        left: 2%;\n        right: auto\n    } 