INSERT INTO {DB_PREFIX}pages (`id`, `type`, `owner_id`, `parent_id`, `template_id`, `item_order`, `hierarchy`, `default_content`, `content_alias`, `show_in_menu`, `active`, `cachable`, `id_hierarchy`, `hierarchy_path`, `metadata`, `titleattribute`, `tabindex`, `accesskey`, `secure`, `last_modified_by`, `url_text`, `lft`, `rgt`, `create_date`, `modified_date`, `extra_params`) VALUES (1, 'CmsPage', 1, -1, 19, 1, '00001', 1, 'homepage', 1, 1, 1, '1', 'home', NULL, NULL, NULL, NULL, NULL, 1, 'homepage', 1, 2, '2010-04-10 22:09:33', '2010-04-25 19:14:35', 'a:11:{s:5:"alias";s:8:"homepage";s:15:"title_attribute";N;s:9:"tab_index";N;s:10:"access_key";N;s:4:"name";a:1:{s:5:"en_US";s:9:"Home Page";}s:9:"menu_text";a:1:{s:5:"en_US";s:9:"Home Page";}s:8:"pagedata";s:0:"";s:15:"disable_wysiwyg";s:1:"0";s:6:"extra1";s:0:"";s:6:"extra2";s:0:"";s:6:"extra3";s:0:"";}');
