INSERT INTO {DB_PREFIX}templates ( TEMPLATE_ID, TEMPLATE_NAME, TEMPLATE_CONTENT, STYLESHEET, ENCODING, ACTIVE, DEFAULT_TEMPLATE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 1, 'Default', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" >\r\n<head>\r\n<title>CMS Made Simple Default Site - {title}</title>\r\n{stylesheet}\r\n</head>\r\n\r\n<body>\r\n\r\n<div class=\"leftsidebar\">\r\n\r\n<div class=\"icons\">\r\n<p><img src=\"images/cms/cmslogo.gif\" alt=\"CMS Logo\" /></p>\r\n</div>\r\n\r\n{{DB_PREFIX}module module=\'cssmenu\'}\r\n</div>\r\n\r\n<div class=\"thebody\">\r\n\r\n<div class=\"heading\">{title}&nbsp;{print showbutton=\"true\"}</div>\r\n<p class=\"breadcrumbs\">{breadcrumbs}</p>\r\n\r\n{content}\r\n</div>\r\n\r\n<!-- Before Footer -->\r\n\r\n<div class=\"thebody\"><div class=\"bottom\">Powered by CMS Made Simple {{DB_PREFIX}version}<br />Think you\'ve found a bug?  <a href=\"http://bugs.cmsmadesimple.org\">Report</a> it.</div></div>\r\n\r\n</body>\r\n</html>', 'body { color: #000000;\r\n       background-color: #fff;\r\n       font-size: 10pt;\r\n       font-family: Tahoma;\r\n       margin: 10; }\r\n\r\ntd.nav     { color: #000000;\r\n             background-color: #fff;\r\n             font-size: 10pt;\r\n             text-align: center;\r\n             font-family: Tahoma }\r\ntd.header  { color: #000000;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\ntd.label   { color: #000000;\r\n             background-color: #ccccff;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\ntd.body    { color: #000000;\r\n\r\n             background-color: #ffffff;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\n\r\ntd.bodyalt { color: #000000;\r\n             background-color: #e9e9e9;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\ntd.footer  { color: #000000;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\n\r\na:link     { color: #A00000;\r\n             text-decoration: none;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\na:visited  { color: #A00000;\r\n             text-decoration: none;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\na:active   { color: #A00000;\r\n             text-decoration: none;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\na:hover    { color: #800000;\r\n             text-decoration: none;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\n\r\n\r\n\r\n\r\n.good     { color: #0033FF;\r\n             font-weight: bold }\r\n.bad      { color: #CC3300;\r\n             font-weight: bold }\r\n\r\ndiv.heading {\r\n    background-color: #64879d; \r\n    color: #EDF2F5;\r\n    padding: 9px; \r\n    text-align: left; \r\n    font-size: 16px; \r\n    font-weight: bold;  \r\n    text-transform: uppercase; \r\n}\r\ndiv.leftsidebar { \r\n    float: left; \r\n    width: 120px; \r\n    background-color: #EDF2F5;\r\n    font-size: 12px; \r\n    padding: 7px; \r\n    border: 1px solid #64879d;\r\n    color: #444444; \r\n}\r\ndiv.thebody { \r\n    padding: 5px; \r\n    margin-left: 150px; \r\n    font-size: 10pt; \r\n    font-family: Tahoma; \r\n}\r\ndiv.bottom {\r\n    background-color: #EDF2F5; \r\n    border: 1px solid #64879d;\r\n    padding: 3px; \r\n    text-align: center;\r\n    font-size: 11; \r\n}\r\np.title { \r\n    font-size: 14pt; \r\n    font-weight: bold; \r\n    margin: 0; \r\n}\r\np.smalltitle { \r\n    font-size: 12pt; \r\n    font-weight: bold; \r\n    margin: 0; \r\n    padding: 4px; \r\n    background-color: #CCCCCC; \r\n    text-transform: uppercase; \r\n}\r\n.breadcrumbs {\r\n    font-size: 8pt; \r\n    margin: 0; \r\n    padding: 2px 0 2px 9px; \r\n    background-color: #CCCCCC; \r\n    text-transform: uppercase;\r\n}\r\n.breadcrumbs a { \r\n    font-size: 8pt; \r\n    text-transform: uppercase; \r\n}\r\np.sectionname { \r\n    font-weight: bold; \r\n    margin: 0;\r\n}\r\n.leftsidebar p.sectionname {\r\n    padding-left: 15px;\r\n}\r\n.icons {\r\n    text-align: center;\r\n}\r\n\r\n/* Menu */\r\n.verbar {\r\n    color: black;\r\n    background-color: #EDF2F5;\r\n}\r\n\r\n.submenu    {\r\n    position: absolute;\r\n    left: 0;\r\n    top: 0;\r\n    visibility: hidden;\r\n}\r\n\r\n.subframe   {\r\n    position: relative;\r\n    display: block;\r\n    background-color: #EDF2F5;\r\n    border: 1px solid #64879d;\r\n}\r\n\r\n.item   {\r\n    padding: 3px;\r\n    text-align: left;\r\n    white-space: nowrap;\r\n}\r\n\r\n.separator  {\r\n    background-color: #ff0000;\r\n    border-top: 1px solid #9e9a91;\r\n    border-bottom: 1px solid #ffffff;\r\n    margin: 2px 1px 2px 1px;    /* top right bottom left */\r\n    height: 0;\r\n    font-size: 1px;\r\n    line-height: 0\r\n}\r\n\r\n.item a {\r\n    position: relative;\r\n    display: block;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:link    {\r\n    color: #000000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:visited {\r\n    color: #000000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:hover   {\r\n    color: #ffffff;\r\n    background-color: #4b6983;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:active  {\r\n    color: #ff0000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item .fwdarr   {\r\n    position: absolute;\r\n    top: 5px;\r\n    right: 7px;\r\n}\r\n', null, 1, 1, '2005-09-21 21:38:05', '2005-09-21 21:38:05' );
