INSERT INTO {DB_PREFIX}css ( CSS_ID, CSS_NAME, CSS_TEXT, MEDIA_TYPE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 41, 'Handheld', '/*********************************************\r\nSample stylesheet for mobile and small screen handheld devices\r\n\r\nJust a simple layout suitable for smaller screens with less \r\nstyling cabapilities and minimal css\r\n\r\nNote: If you dont want to support mobile devices you can\r\nsafely remove this stylesheet.\r\n*********************************************/\r\n/* remove all padding and margins and set width to 100%. This should be default for handheld devices but its good to set these explicitly */\r\nbody {\r\nmargin:0;\r\npadding:0;\r\nwidth:100%;\r\n}\r\n\r\n/* hide accessibility noprint and definition */\r\n.accessibility,\r\n.noprint,\r\ndfn {\r\ndisplay:none;\r\n}\r\n\r\n/* dont want to download image for header so just set bg color */\r\ndiv#header,\r\ndiv#footer {\r\nbackground-color: #385C72;  \r\ncolor: #fff;\r\ntext-align:center;\r\n}\r\n\r\n/* text colors for header and footer */\r\ndiv#header a,\r\ndiv#footer a {\r\ncolor: #fff;\r\n}\r\n\r\n/* this doesnt look as nice, but takes less space */\r\ndiv#menu_vert ul li,\r\ndiv#menu_horiz ul li {\r\ndisplay:inline;\r\n}\r\n\r\n/* small border at the bottom to have some indicator */\r\ndiv#menu_vert ul,\r\ndiv#menu_horiz ul {\r\nborder-bottom:1px solid #fff;\r\n}\r\n\r\n/* save some space */\r\ndiv.breadcrumbs {\r\ndisplay:none;\r\n}', 'handheld', '2006-07-25 21:22:32', '2006-07-25 21:22:32' );
INSERT INTO {DB_PREFIX}css ( CSS_ID, CSS_NAME, CSS_TEXT, MEDIA_TYPE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 30, 'Print', '/*\r\nSections that are hidden when printing the page. We only want the content printed.\r\n*/\r\n\r\n\r\nbody {\r\ncolor: #000 !important; /* we want everything in black */\r\nbackground-color:#fff !important; /* on white background */\r\nfont-family:arial; /* arial is nice to read ;) */\r\nborder:0 !important; /* no borders thanks */\r\n}\r\n\r\n/* This affects every tag */\r\n* {\r\nborder:0 !important; /* again no borders on printouts */\r\n}\r\n\r\n/* \r\nno need for accessibility on printout. \r\nMark all your elements in content you \r\ndont want to get printed with class=\"noprint\"\r\n*/\r\n.accessibility,\r\n.noprint\r\n {\r\ndisplay:none !important; \r\n}\r\n\r\n/* \r\nremove all width constraints from content area\r\n*/\r\ndiv#content,\r\ndiv#main {\r\ndisplay:block !important;\r\nwidth:100% !important;\r\nborder:0 !important;\r\npadding:1em !important;\r\n}\r\n\r\n/* hide everything else! */\r\ndiv#header,\r\ndiv#header h1 a,\r\ndiv.breadcrumbs,\r\ndiv#search,\r\ndiv#footer,\r\ndiv#menu_vert,\r\ndiv#news,\r\ndiv.right49,\r\ndiv.left49,\r\ndiv#sidebar  {\r\n   display: none !important;\r\n}\r\n\r\nimg {\r\nfloat:none; /* this makes images couse a pagebreak if it doesnt fit on the page */\r\n}', 'print', '2006-07-25 21:22:32', '2006-07-25 21:22:32' );
INSERT INTO {DB_PREFIX}css ( CSS_ID, CSS_NAME, CSS_TEXT, MEDIA_TYPE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 31, 'Accessibility and cross-browser tools', '/* accessibility */\r\n\r\n/* \r\nmenu links accesskeys \r\n*/\r\nspan.accesskey {\r\n   text-decoration:none;\r\n}\r\n\r\n/* \r\naccessibility divs are hidden by default \r\ntext, screenreaders and such will show these \r\n*/\r\n.accessibility, hr {\r\n   position: absolute;\r\n   top: -999em;\r\n   left: -999em;\r\n}\r\n\r\n/* \r\ndefinition tags are also hidden \r\nthese are also used for menu links    \r\n*/\r\ndfn {\r\n   position: absolute;\r\n   left: -1000px;\r\n   top: -1000px;\r\n   width: 0;\r\n   height: 0;\r\n   overflow: hidden;\r\n   display: inline;\r\n}\r\n/* end accessibility */\r\n\r\n\r\n/* wiki style external links */\r\n/* external links will have \"(external link)\" text added, lets hide it */\r\na.external span {\r\n  position: absolute;\r\n  left: -5000px;\r\n  width: 4000px;\r\n}\r\n\r\n/* make some room for the image */\r\na.external {\r\n/* css shorthand rules, read: first top padding 0 then right padding 12px then bottom then right */\r\n  padding: 0 12px 0 0;\r\n}\r\n/* colors for external links */\r\na.external:link {\r\n  color: #18507C;\r\n/* background image for the link to show wiki style arrow */\r\n  background: url(images/cms/external.gif) no-repeat 100% 0;\r\n}\r\na.external:visited {\r\n  color: #18507C; /* a different color can be used for visited external links */\r\n\r\n/* \r\nSet the last 0 to -100px to use that part of the external.gif image for different color for active links \r\nexternal.gif is actually 300px tall, we can use different positions of the image to simulate rollover image changes.\r\n*/\r\n  background: url(images/cms/external.gif) no-repeat 100% 0; \r\n}\r\n\r\na.external:hover {\r\n  color: #18507C;\r\n/* Set the last 0 to -200px to use that part of the external.gif image for different color on hover */\r\n  background: url(images/cms/external.gif) no-repeat 100% 0; \r\n  background-color: #C3D4DF;\r\n}\r\n/* end wiki style external links */\r\n\r\n\r\n/* clearing */\r\n/* \r\nclearfix is a hack for divs that hold floated elements. it will force the holding div to span all the way down to last floated item.\r\nWe strongly recommend against using this as it is a hack and might not render correctly but it is included here for convenience.\r\nDo not edit if you dont know what you are doing\r\n*/\r\n	.clearfix:after {\r\n		content: \".\"; \r\n		display: block; \r\n		height: 0; \r\n		clear: both; \r\n		visibility: hidden;\r\n	}\r\n	* html>body .clearfix {\r\n		display: inline-block; \r\n		width: 100%;\r\n	}\r\n	\r\n	* html .clearfix {\r\n		/* Hides from IE-mac \\*/\r\n		height: 1%;\r\n		/* End hide from IE-mac */\r\n	}\r\n\r\n/* end clearing */', 'screen', '2006-07-25 21:22:32', '2006-07-25 21:22:32' );
INSERT INTO {DB_PREFIX}css ( CSS_ID, CSS_NAME, CSS_TEXT, MEDIA_TYPE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 32, 'Layout: Left sidebar + 1 column', '/*****************\r\nbrowsers interpret margin and padding a little differently, \r\nwe\'ll remove all default padding and margins and\r\nset them later on\r\n******************/\r\n* {\r\nmargin:0;\r\npadding:0;\r\n}\r\n\r\n/*\r\nSet initial font styles\r\n*/\r\nbody {\r\n   text-align: left;\r\n   font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\r\n   font-size: 75.01%;\r\n   line-height: 1em;\r\n}\r\n\r\n/*\r\nset font size for all divs,\r\nthis overrides some body rules\r\n*/\r\ndiv {\r\n   font-size: 1em;\r\n}\r\n\r\n/*\r\nif img is inside \"a\" it would have \r\nborders, we don\'t want that\r\n*/\r\nimg {\r\n   border: 0;\r\n}\r\n\r\n/*\r\ndefault link styles\r\n*/\r\n/* set all links to have underline and bluish color */\r\na,\r\na:link \r\na:active {\r\n   text-decoration: underline;\r\n/* css validation will give a warning if color is set without background color. this will explicitly tell this element to inherit bg colour from parent element */\r\n   background-color: inherit; \r\n   color: #18507C; \r\n}\r\n\r\na:visited {\r\n   text-decoration: underline;\r\n   background-color: inherit;\r\n  color: #18507C;                /* a different color can be used for visited links */\r\n}\r\n\r\n\r\n/* remove underline on hover and change color */\r\na:hover {\r\n   text-decoration: none;\r\n   background-color: #C3D4DF;\r\n   color: #385C72;\r\n}\r\n\r\n/*****************\r\nbasic layout \r\n*****************/\r\nbody {\r\n   background-color: #ccc;\r\n   color: #333;\r\n   margin:1em;    /* gives some air for the pagewrapper */\r\n}\r\n\r\n/* center wrapper, min max width */\r\ndiv#pagewrapper {\r\n   border: 1px solid black;\r\n   margin: 0 auto;       /* this centers wrapper */\r\n   max-width: 80em;   /* IE wont understand these, so we will use javascript magick */\r\n   min-width: 60em;\r\n   background-color: #fff;;\r\n   color: black;\r\n}\r\n\r\n\r\n/*** header ***\r\nwe will hide text and replace it with a image\r\nwe need to assign a height for it so that the image wont cut off\r\n*/\r\ndiv#header {\r\n   height: 80px;    /* adjust according your image size */\r\n   background: #385C72;           \r\n}\r\n\r\ndiv#header h1 a {\r\n/* you can set your own image here */\r\n   background: #385C72 url(images/cms/logo1.gif) no-repeat 0 12px; \r\n   display: block;\r\n   height: 80px;             /* adjust according your image size */\r\n   text-indent: -999em;  /* this hides the text */\r\n   text-decoration:none; /* old firefox would have shown underline for the link, this explicitly hides it */\r\n}\r\n\r\n/* position for the search box */\r\ndiv#search {\r\n   float: right;\r\n   width: 23em;    /* enough width for the search input box */\r\n   text-align: right;\r\n   padding: 0.6em 0 0.2em 0;\r\n   margin: 0 1em;\r\n}\r\n\r\ndiv.breadcrumbs {\r\n   padding: 1em 0 1.2em 0; /* CSS short hand rule first value is top then right, bottom and left */\r\n   font-size: 90%;        /* its good to set fontsizes to be relative, this way viewer can change his/her fontsize */\r\n   margin: 0 1em;        /* css shorthand rule will be opened to be \"0 1em 0 1em\" */\r\n   border-bottom: 1px dotted #000;\r\n}\r\n\r\ndiv#content {\r\n   margin: 1.5em auto 2em 0;   /* some air above and under menu and content */\r\n}\r\n\r\n\r\ndiv#main {\r\n   margin-left: 29%; /* this will give room for sidebar to be on the left side, make sure this space is bigger than sidebar width */\r\n   margin-right: 2%; /* and some air on the right */\r\n}\r\n\r\ndiv#sidebar {\r\n   float: left;        /* set sidebar on the left side. Change to right to float it right instead. */\r\n   width: 26%;     /* sidebar width, if you change this please also change #main margins */\r\n   display: inline;  /* FIX IE double margin bug */\r\n   margin-left: 0;\r\n}\r\n\r\ndiv#footer {\r\n   clear: both;       /* keep footer below content and menu */\r\n   color: #fff;\r\n   background-color: #385C72; /* same bg color as in header */\r\n}\r\n\r\ndiv#footer p {\r\n   font-size: 0.8em;\r\n   padding: 1.5em;       /* some air for footer */\r\n   text-align: center;  /* centered text */\r\n   margin: 0;\r\n}\r\n\r\ndiv#footer p a {\r\n   color: #fff; /* needed becouse footer link would be same color as background otherwise */\r\n}\r\n\r\n/* as we hid all hr for accessibility we create new hr with extra div element */\r\ndiv.hr {\r\n   height: 1px;\r\n   padding: 1em;\r\n   border-bottom: 1px dotted black;\r\n   margin: 1em;\r\n}\r\n\r\n/* relational links under content */\r\ndiv.left49 {\r\n  width: 49%; /* 50% for both left and right might lead to rounding error on some browser */\r\n}\r\n\r\ndiv.right49 {\r\n  float: right;\r\n  width: 49%;\r\n  text-align: right;\r\n}\r\n\r\n\r\n\r\n\r\n/********************\r\nCONTENT STYLING\r\n*********************/\r\ndiv#content {\r\n\r\n}\r\n\r\n/* HEADINGS */\r\ndiv#content h1 {\r\n   font-size: 2em; /* font size for h1 */\r\n   line-height: 1em;\r\n   margin: 0;\r\n}\r\ndiv#content h2 {\r\n	color: #294B5F; \r\n	font-size: 1.5em; \r\n	text-align: left; \r\n/* some air around the text */\r\n	padding-left: 0.5em;\r\n	padding-bottom: 1px;\r\n/* set borders around header */\r\n	border-bottom: 1px solid #e7ab0b; \r\n	border-left: 1.1em solid #e7ab0b; \r\n        line-height: 1.5em;\r\n/* and some air under the border */\r\n        margin: 0 0 0.5em 0;\r\n}\r\ndiv#content h3 {\r\n   color: #294B5F; \r\n   font-size: 1.3em;\r\n   line-height: 1.3em;\r\n   margin: 0 0 0.5em 0;\r\n}\r\ndiv#content h4 {\r\n   color: #294B5F; \r\n   font-size: 1.2em;\r\n   line-height: 1.3em;\r\n   margin: 0 0 0.25em 0;\r\n}\r\ndiv#content h5 {\r\n   color: #294B5F; \r\n   font-size: 1.1em;\r\n   line-height: 1.3em;\r\n   margin: 0 0 0.25em 0;\r\n}\r\nh6 {\r\n   color: #294B5F; \r\n   font-size: 1em;\r\n   line-height: 1.3em;\r\n   margin: 0 0 0.25em 0;\r\n}\r\n/* END HEADINGS */\r\n\r\n/* TEXT */\r\np {\r\n   font-size: 1em;\r\n   margin: 0 0 1.5em 0; /* some air around p elements */\r\n   line-height: 1.4em;\r\n   padding: 0;\r\n}\r\n\r\nblockquote {\r\n   border-left: 10px solid #ddd;\r\n   margin-left: 10px;\r\n}\r\nstrong, b {\r\n/* explicit setting for these */\r\n   font-weight: bold;\r\n}\r\nem, i {\r\n/* explicit setting for these */\r\n   font-style:italic;\r\n}\r\n\r\n/* Wrapping text in <code> tags. Makes CSS not validate */\r\ncode, pre {\r\n white-space: pre-wrap;       /* css-3 */\r\n white-space: -moz-pre-wrap;  /* Mozilla, since 1999 */\r\n white-space: -pre-wrap;      /* Opera 4-6 */\r\n white-space: -o-pre-wrap;    /* Opera 7 */\r\n word-wrap: break-word;       /* Internet Explorer 5.5+ */\r\n font-family: \"Courier New\", Courier, monospace;\r\n font-size: 1em;\r\n}\r\n\r\npre {\r\n   border: 1px solid #000;  /* black border for pre blocks */\r\n   background-color: #ddd;\r\n   margin: 0 1em 1em 1em;\r\n   padding: 0.5em;\r\n   line-height: 1.5em;\r\n   font-size: 90%;\r\n\r\n\r\n}\r\n\r\n/* Separating the divs on the template explanation page, with some bottom-border */\r\ndiv.templatecode {\r\n  margin: 0 0 2.5em;\r\n}\r\n\r\n/* END TEXT */\r\n\r\n/* LISTS */\r\n/* lists in content need some margins to look nice */\r\ndiv#main ul,\r\ndiv#main ol,\r\ndiv#main dl {\r\n   font-size: 1.0em;\r\n   line-height: 1.4em;\r\n   margin: 0 0 1.5em 0;\r\n}\r\n\r\ndiv#main ul li,\r\ndiv#main ol li {\r\n   margin: 0 0 0.25em 3em;\r\n}\r\n\r\n/* definition lists topics on bold */\r\ndiv#main dl dt {\r\n   font-weight: bold;\r\n   margin: 0 0 0 1em;\r\n}\r\ndiv#main dl dd {\r\n   margin: 0 0 1em 1em;\r\n}\r\n\r\ndiv#main dl {\r\n  margin-bottom: 2em;\r\n  padding-bottom: 1em;\r\n  border-bottom: 1px solid #c0c0c0;\r\n}\r\n\r\n\r\n/* END LISTS */', 'screen', '2006-07-25 21:22:32', '2006-07-25 21:22:32' );
INSERT INTO {DB_PREFIX}css ( CSS_ID, CSS_NAME, CSS_TEXT, MEDIA_TYPE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 35, 'Module: News', 'div#news {\r\n  margin: 2em 0 1em 1em;  /* margin for the entire div surrounding the news list */\r\n  border: 1px solid #000; \r\n  background: #FFE9AF; \r\n}\r\n\r\ndiv#news h2 {\r\n   line-height: 2em;\r\n   background: #fff;\r\n}\r\n\r\n.NewsSummary {\r\n    padding: 0.5em 0.5em 1em; /* padding for the news article summary */\r\n    margin: 0 0.5em 1em 0.5em; /* margin to the bottom of the news article summary */\r\n    border-bottom: 1px solid #ccc; \r\n}\r\n\r\n.NewsSummaryPostdate {\r\n  font-size: 90%;\r\n  font-weight: bold;\r\n }\r\n\r\n.NewsSummaryLink {\r\n    font-weight: bold;\r\n    padding-top: 0.2em;\r\n}\r\n\r\n.NewsSummaryCategory {\r\n  font-style: italic;\r\n  margin: 5px 0;\r\n }\r\n\r\n.NewsSummaryAuthor {\r\n  font-style: italic;\r\n  padding-bottom: 0.5em;\r\n}\r\n\r\n.NewsSummarySummary, .NewsSummaryContent {\r\n  line-height: 140%;\r\n }\r\n\r\n.NewsSummaryMorelink {\r\n  padding-top: 0.5em;\r\n}\r\n\r\n#NewsPostDetailDate {\r\n  font-size: 90%;\r\n  margin-bottom: 5px;\r\n  font-weight: bold;\r\n  }\r\n\r\n#NewsPostDetailSummary {\r\n   line-height: 150%;\r\n   }\r\n\r\n#NewsPostDetailCategory {\r\n  font-style: italic;\r\n  border-top: 1px solid #ccc;\r\n  margin-top: 0.5em;\r\n  padding: 0.2em 0;\r\n}\r\n\r\n#NewsPostDetailContent {\r\n  margin-bottom: 15px;\r\n  line-height: 150%;\r\n  }\r\n\r\n#NewsPostDetailAuthor {\r\n\r\n  padding-bottom: 1.5em;\r\n  font-style: italic;\r\n}\r\n\r\n\r\n/* to add specific style to the below divs, uncomment them. */\r\n\r\n/* \r\n#NewsPostDetailTitle {}\r\n#NewsPostDetailHorizRule {}\r\n#NewsPostDetailPrintLink {}\r\n#NewsPostDetailReturnLink {}\r\n*/', 'screen', '2006-07-25 21:22:32', '2006-07-25 21:22:32' );
INSERT INTO {DB_PREFIX}css ( CSS_ID, CSS_NAME, CSS_TEXT, MEDIA_TYPE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 36, 'Navigation: Simple - Vertical', '/********************\r\nMENU\r\n*********************/\r\n#menu_vert {\r\n   padding-left: 0;\r\n   margin-left: 1em;\r\n}\r\n\r\n\r\n/* third level has some padding to have it stand out */\r\ndiv#menu_vert ul ul ul {\r\n   padding-bottom: 0.5em;\r\n}\r\n\r\n/* menu li block */\r\n#menu_vert li {\r\n   list-style: none;\r\n   margin: 0;\r\n   border-bottom: 1px solid #c0c0c0;\r\n   display: block;\r\n}\r\n\r\n#menu_vert ul ul li {\r\n   border: none;\r\n}\r\n\r\n/** fix stupid ie bug with display:block; **/\r\n* html #menu_vert li a { height: 1%; } \r\n* html #menu_vert li hr { height: 1%; }\r\n/** end fix **/\r\n\r\n/* first level links */\r\ndiv#menu_vert a {\r\n   text-decoration:none; /* no underline for links */\r\n   display: block; /* IE has problems with this, fixed above */\r\n   padding: 0.8em 0.5em 0.8em 1.5em; /* some air for it */\r\n   color: #18507C; /* this will be link color for all levels */\r\n   background: url(images/cms/arrow-right.gif) no-repeat 0.5em center; \r\n   background: transparent; \r\n   min-height:1em; /* Fixes IE7 whitespace bug */ \r\n}\r\n\r\n/* next level links, more padding and smaller font */\r\ndiv#menu_vert ul ul a {\r\n   font-size: 90%;\r\n   padding: 0.5em 0.5em 0.5em 2.8em;\r\n   background-position: 1.5em center;\r\n}\r\n\r\n/* third level links, more padding */\r\ndiv#menu_vert ul ul ul a {\r\n   padding: 0.3em 0.5em 0.3em 4.3em;\r\n   background: url(images/cms/dash.gif) no-repeat 2.8em center; \r\n}\r\n\r\n/* hover state for all links */\r\ndiv#menu_vert a:hover {\r\n   background-color: #C3D4DF;\r\n}\r\ndiv#menu_vert a.activeparent:hover {\r\n   background-color: #C3D4DF;\r\n   color: #18507C;\r\n}\r\n\r\n/* \r\nactive parent, that is the first-level parent \r\nof a child page that is the current page \r\n*/\r\ndiv#menu_vert li a.activeparent {\r\n   background: url(images/cms/arrow-down.gif) no-repeat 0.4em center; \r\n   background-color: #385C72;\r\n   color: #fff;\r\n}\r\n\r\ndiv#menu_vert ul ul li a.activeparent {\r\n   background-position: 1.5em center; \r\n   background-color: transparent;\r\n   color: #18507C;\r\n}\r\n\r\n\r\n/* \r\ncurrent pages in the default Menu Manager \r\ntemplate are unclickable. This is for current page on first level \r\n*/\r\ndiv#menu_vert ul h3 {\r\n   background: url(images/cms/arrow-right-active.gif) no-repeat 0.4em center;\r\n   background-color: #385C72;\r\n   display: block; \r\n   padding: 0.8em 0.5em 0.8em 1.5em;  /* some air for it */\r\n   color: #fff;                                 /* this will be link color for all levels */\r\n   font-size: 1em;                           /* instead of the normal font size for <h3> */\r\n   margin: 0;                                  /* as <h3> normally has some margin by default */\r\n}\r\n\r\n/* \r\nnext level current pages, more padding, \r\nsmaller font and no background color or bottom border \r\n*/\r\ndiv#menu_vert ul ul h3 {\r\n   font-size: 90%;\r\n   padding: 0.3em 0.5em 0.3em 2.8em;\r\n   background-position: 1.4em center;\r\n   background-color: transparent;\r\n   border-bottom: none;\r\n   color: #000;     \r\n}\r\n\r\n/* current page on third level, more padding */\r\ndiv#menu_vert ul ul ul h3 {\r\n   padding: 0.3em 0.5em 0.3em 4.3em;\r\n   background: url(images/cms/arrow-right-active.gif) no-repeat 2.7em center; \r\n}\r\n\r\n/* section header */\r\ndiv#menu_vert li.sectionheader {\r\n   border-right: none;\r\n   font-size: 130%;\r\n   font-weight: bold;\r\n   padding: 1.5em 0 0.8em 0;\r\n   background-color: #fff;\r\n   line-height: 1em;\r\n   margin: 0;\r\n   text-align:center;\r\n}\r\n\r\n\r\n\r\n/* separator */\r\ndiv#menu_vert li.separator {\r\n   height: 1px !important;\r\n   margin-top: -1px;\r\n   margin-bottom: 0;\r\n   padding:2px 0 2px 0;\r\n   background-color: #000;\r\n   overflow:hidden !important;\r\n   line-height:1px !important;\r\n   font-size:1px; /* for ie */\r\n}\r\n\r\ndiv#menu_vert li.separator hr {\r\n   display: none; /* this is for accessibility */\r\n}', 'screen', '2006-07-25 21:22:32', '2006-07-25 21:22:32' );
