INSERT INTO {DB_PREFIX}templates ( TEMPLATE_ID, TEMPLATE_NAME, TEMPLATE_CONTENT, STYLESHEET, ENCODING, ACTIVE, DEFAULT_TEMPLATE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 17, 'Left simple navigation + 1 column', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\r\n\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en\">\r\n<!-- Change lang=\"en\" to the language of your site -->\r\n\r\n<head>\r\n\r\n<title>{sitename} - {title}</title>\r\n<!-- The sitename is changed in Site Admin/Global settings. {title} is the name of each page -->\r\n\r\n{metadata}\r\n<!-- Don\'t remove this! Metadata is entered in Site Admin/Global settings. -->\r\n\r\n{stylesheet}\r\n<!-- This is how all the stylesheets attached to this template are linked to -->\r\n\r\n{cms_selflink dir=\"start\" rellink=1}\r\n{cms_selflink dir=\"prev\" rellink=1}\r\n{cms_selflink dir=\"next\" rellink=1}\r\n<!-- Relational links for interconnections between pages, good for accessibility and Search Engine Optmization -->\r\n\r\n\r\n{literal}\r\n<script type=\"text/JavaScript\">\r\n<!--\r\n//pass min and max -measured against window width\r\nfunction P7_MinMaxW(a,b){\r\n	var nw=\"auto\",w=document.documentElement.clientWidth;\r\n	if(w>=b){nw=b+\"px\";}if(w<=a){nw=a+\"px\";}return nw;\r\n}\r\n//-->\r\n</script>\r\n<!--[if lte IE 6]>\r\n<style type=\"text/css\">\r\n#pagewrapper {width:expression(P7_MinMaxW(720,950));}\r\n#container {height: 1%;}\r\n</style>\r\n<![endif]-->\r\n{/literal}\r\n<!-- The min and max page width for Internet Explorer is set here. For other browsers it\'s in the stylesheet \"Layout: Left sidebar + 1 column\" -->\r\n\r\n\r\n</head>\r\n\r\n<body>\r\n\r\n<div id=\"pagewrapper\">\r\n\r\n    <!-- start accessibility skip links -->\r\n    <ul class=\"accessibility\">\r\n      <li>{anchor anchor=\'menu_vert\' title=\'Skip to navigation\' accesskey=\'n\' text=\'Skip to navigation\'}</li>\r\n      <li>{anchor anchor=\'main\' title=\'Skip to content\' accesskey=\'s\' text=\'Skip to content\'}</li>\r\n    </ul>\r\n    <!-- end accessibility skip links -->\r\n\r\n\r\n    <hr class=\"accessibility\" />\r\n    <!-- Horizontal ruler that is hidden for visual browsers by CSS -->\r\n\r\n\r\n   <!-- Start Header, with logo image that links to the default start page. Logo image is changed in the stylesheet  \"For template: Left menu + 1 column\" -->\r\n   <div id=\"header\">\r\n           <h1>{cms_selflink dir=\"start\" text=\"$sitename\"}</h1>\r\n   <hr class=\"accessibility\" />\r\n   </div>\r\n   <!-- End Header -->\r\n\r\n\r\n   <!-- Start Search-->\r\n   <div id=\"search\">\r\n          {search}\r\n   </div>\r\n   <!-- End Search -->\r\n\r\n\r\n   <!-- Start Breadcrumbs -->\r\n   <div class=\"breadcrumbs\">\r\n        {breadcrumbs starttext=\'You are here\' root=\'Home\' delimiter=\'&raquo;\'}\r\n   <hr class=\"accessibility\" />\r\n   </div>\r\n   <!-- End Breadcrumbs -->\r\n\r\n\r\n   <!-- Start Content (Navigation and Content columns) -->\r\n   <div id=\"content\">\r\n\r\n      <!-- Start Sidebar -->\r\n      <div id=\"sidebar\">\r\n\r\n      <!-- Start Navigation -->\r\n      <div id=\"menu_vert\">\r\n         <h2 class=\"accessibility\">Navigation</h2>\r\n         {menu template=\'simple_navigation.tpl\' collapse=\'1\'}\r\n      </div>\r\n      <!-- End Navigation -->\r\n\r\n      <!-- Start News -->\r\n      <div id=\"news\">\r\n         <h2>News</h2>\r\n          {news number=\'3\' detailpage=\'news\'}\r\n      </div>\r\n      <!-- End News -->\r\n\r\n      </div>\r\n      <!-- End Sidebar -->\r\n\r\n\r\n      <!-- Start Content Area -->\r\n      <div id=\"main\">\r\n         <div style=\"float: right;\">{print showbutton=true script=true}</div>\r\n         <h2>{title}</h2>\r\n         {content} <br />\r\n\r\n         <!-- Start relational links -->\r\n	 <div class=\"hr\"></div>\r\n	 <div class=\"right49\">\r\n	 	<p>{anchor anchor=\'main\' text=\'^ Top\'}</p>\r\n	 </div>\r\n         <div class=\"left49\">\r\n            <p>{cms_selflink dir=\"previous\" label=\"Previous page: \"} <br />\r\n            {cms_selflink dir=\"next\"}</p>\r\n         </div>\r\n         <!-- The label parameter doesn\'t need to be there if you\'re using English, but is here to show how it\'s used if you don\'t want the English text \"Previous page\" -->\r\n	 <!-- End relational links -->\r\n\r\n      <hr class=\"accessibility\" />\r\n      </div>\r\n	  <!-- End Content Area -->\r\n\r\n\r\n   </div>\r\n   <!-- End Content -->\r\n\r\n\r\n   <!-- Start Footer. Edit the footer in the Global Content Block called \"footer\" -->\r\n   <div id=\"footer\">\r\n      {global_content name=\'footer\'}\r\n   </div>   \r\n   <!-- End Footer  -->\r\n\r\n\r\n</div><!-- end pagewrapper -->\r\n</body>\r\n</html>', null, null, 1, 1, '2006-07-25 21:22:33', '2006-07-25 21:22:33' );
