INSERT INTO {DB_PREFIX}module_templates ( MODULE_NAME, TEMPLATE_NAME, CONTENT, CREATE_DATE, MODIFIED_DATE ) VALUES ( 'News', 'displaysummary', '<!-- Start News Display Template -->\r\n{foreach from=$items item=entry}\r\n\r\n<div class=\"NewsSummaryLink\">\r\n	{$entry->titlelink}\r\n</div>\r\n\r\n<div class=\"NewsSummaryCategory\">\r\n	({$entry->category})\r\n</div\r\n\r\n{if $entry->formatpostdate}\r\n	<div class=\"NewsSummaryPostdate\">\r\n		{$entry->formatpostdate}\r\n	</div\r\n{/if}\r\n\r\n{if $entry->summary}\r\n	<div class=\"NewsSummarySummary\">\r\n		{$entry->summary}\r\n	</div>\r\n\r\n	<div class=\"NewsSummaryMorelink\">\r\n		[{$entry->morelink}]\r\n	</div>\r\n\r\n{else if $entry->content}\r\n\r\n	<div class=\"NewsSummaryContent\">\r\n		{$entry->content}\r\n	</div>\r\n{/if}\r\n\r\n\r\n{/foreach}\r\n<!-- End News Display Template -->', '2005-11-29 09:26:29', '2005-11-29 09:26:29' );
