INSERT INTO {DB_PREFIX}htmlblobs ( HTMLBLOB_ID, HTMLBLOB_NAME, HTML, OWNER, CREATE_DATE, MODIFIED_DATE ) VALUES ( 5, 'JavaScript for IE page width', '{literal}\r\n<script type=\"text/JavaScript\">\r\n<!--\r\n//pass min and max -measured against window width\r\nfunction P7_MinMaxW(a,b){\r\n	var nw=\"auto\",w=document.documentElement.clientWidth;\r\n	if(w>=b){nw=b+\"px\";}if(w<=a){nw=a+\"px\";}return nw;\r\n}\r\n//-->\r\n</script>\r\n<!--[if lte IE 6]>\r\n<style type=\"text/css\">\r\n#pagewrapper {width:expression(P7_MinMaxW(720,950));}\r\n#container {height: 1%;}\r\n</style>\r\n<![endif]-->\r\n{/literal}', 1, '2006-01-26 17:40:01', '2006-01-26 17:40:01' );
INSERT INTO {DB_PREFIX}module_templates ( MODULE_NAME, TEMPLATE_NAME, CONTENT, CREATE_DATE, MODIFIED_DATE ) VALUES ( 'News', 'displaysummary', '<!-- Start News Display Template -->\n{foreach from=$items item=entry}\n\n<div class=\"NewsSummaryLink\">\n	{$entry->titlelink}\n</div>\n\n<div class=\"NewsSummaryCategory\">\n	{$entry->category}\n</div>\n\n{if $entry->formatpostdate}\n	<div class=\"NewsSummaryPostdate\">\n		{$entry->formatpostdate}\n	</div>\n{/if}\n\n<div class=\"NewsSummaryAuthor\">\n  {$entry->author}({$entry->author_id})\n</div>\n\n{if $entry->summary}\n	<div class=\"NewsSummarySummary\">\n		{eval var=$entry->summary}\n	</div>\n\n	<div class=\"NewsSummaryMorelink\">\n		[{$entry->morelink}]\n	</div>\n\n{else if $entry->content}\n\n	<div class=\"NewsSummaryContent\">\n		{eval var=$entry->content}\n	</div>\n{/if}\n\n{/foreach}\n<!-- End News Display Template -->', '2006-01-26 17:40:01', '2006-01-26 17:40:01' );
INSERT INTO {DB_PREFIX}module_templates ( MODULE_NAME, TEMPLATE_NAME, CONTENT, CREATE_DATE, MODIFIED_DATE ) VALUES ( 'News', 'displaydetail', '<h3 id=\"NewsPostDetailTitle\">{$entry->title}</h3>\n\n<hr id=\"NewsPostDetailHorizRule\">\n\n{if $entry->category}\n	<div id=\"NewsPostDetailCategory\">\n		{$entry->category}\n	</div>\n{/if}\n\n{if $entry->formatpostdate}\n	<div id=\"NewsPostDetailDate\">\n		Posted: {$entry->formatpostdate}\n	</div>\n{/if}\n\n{if $entry->summary}\n	<div id=\"NewsPostDetailSummary\">\n		<strong>\n			{eval var=$entry->summary}\n		</strong>\n	</div>\n{/if}\n\n<div class=\"NewsSummaryAuthor\">\n  {$entry->author}({$entry->author_id})\n</div>\n\n<div id=\"NewsPostDetailContent\">\n	{eval var=$entry->content}\n</div>\n\n<div>\n	{$entry->printlink}\n</div>\n{if $return_url != \"\"}\n<div id=\"NewsReturnLink\">{$return_url}</div>\n{/if}', '2006-01-26 17:40:01', '2006-01-26 17:40:01' );
