INSERT INTO {DB_PREFIX}module_news ( NEWS_ID, NEWS_TITLE, NEWS_DATA, NEWS_DATE, START_TIME, END_TIME, ICON, CREATE_DATE, MODIFIED_DATE ) VALUES ( 1, 'News Module Installed', 'The news module was installed.  Exciting.', '2004-10-03 22:34:01', null, null, null, '2004-10-03 22:37:51', '2004-10-03 22:37:51' );
