INSERT INTO {DB_PREFIX}templates ( TEMPLATE_ID, TEMPLATE_NAME, TEMPLATE_CONTENT, STYLESHEET, ACTIVE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 1, 'Default', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" >\r\n<head>\r\n<title>CMS Made Simple Default Site - {title}</title>\r\n{stylesheet}\r\n</head>\r\n\r\n<body>\r\n\r\n<div class=\"leftsidebar\">\r\n\r\n<div class=\"icons\">\r\n<p><img src=\"images/cms/cmslogo.gif\" alt=\"CMS Logo\" /></p>\r\n</div>\r\n\r\n{dhtmlmenu}\r\n</div>\r\n\r\n<div class=\"thebody\">\r\n\r\n<div class=\"heading\">{title}&nbsp;{print showbutton="true"}</div>\r\n\r\n{content}\r\n</div>\r\n\r\n<!-- Before Footer -->\r\n\r\n<div class=\"thebody\"><div class=\"bottom\">{footer}</div></div>\r\n\r\n</body>\r\n</html>', 'body { color: #000000;\r\n       background-color: #fff;\r\n       font-size: 10pt;\r\n       font-family: Tahoma;\r\n       margin: 10; }\r\n\r\ntd.nav     { color: #000000;\r\n             background-color: #fff;\r\n             font-size: 10pt;\r\n             text-align: center;\r\n             font-family: Tahoma }\r\ntd.header  { color: #000000;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\ntd.label   { color: #000000;\r\n             background-color: #ccccff;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\ntd.body    { color: #000000;\r\n\r\n             background-color: #ffffff;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\n\r\ntd.bodyalt { color: #000000;\r\n             background-color: #e9e9e9;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\ntd.footer  { color: #000000;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\n\r\na:link     { color: #002060;\r\n             text-decoration: none;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\na:visited  { color: #333333;\r\n             text-decoration: none;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\na:active   { color: #606060;\r\n             text-decoration: none;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\na:hover    { color: #700000;\r\n             text-decoration: none;\r\n             font-size: 10pt;\r\n             font-family: Tahoma }\r\n\r\n\r\n\r\n\r\n.good     { color: #0033FF;\r\n             font-weight: bold }\r\n.bad      { color: #CC3300;\r\n             font-weight: bold }\r\n\r\ndiv.heading {\r\n    background-color: #64879d; \r\n    color: #EDF2F5;\r\n    padding: 9px; \r\n    text-align: left; \r\n    font-size: 16px; \r\n    font-weight: bold;  \r\n    text-transform: uppercase; \r\n}\r\ndiv.leftsidebar { \r\n    float: left; \r\n    width: 120px; \r\n    background-color: #EDF2F5;\r\n    font-size: 12px; \r\n    padding: 7px; \r\n    border: 1px solid #64879d;\r\n    color: #444444; \r\n}\r\ndiv.thebody { \r\n    padding: 5px; \r\n    margin-left: 150px; \r\n    font-size: 10pt; \r\n    font-family: Tahoma; \r\n}\r\ndiv.bottom {\r\n    background-color: #EDF2F5; \r\n    border: 1px solid #64879d;\r\n    padding: 3px; \r\n    text-align: center;\r\n    font-size: 11; \r\n}\r\np.title { \r\n    font-size: 14pt; \r\n    font-weight: bold; \r\n    margin: 0; \r\n}\r\np.smalltitle { \r\n    font-size: 12pt; \r\n    font-weight: bold; \r\n    margin: 0; \r\n    padding: 4px; \r\n    background-color: #CCCCCC; \r\n    text-transform: uppercase; \r\n}\r\np.sectionname { \r\n    font-weight: bold; \r\n    margin: 0;\r\n}\r\n.leftsidebar p.sectionname {\r\n    padding-left: 15px;\r\n}\r\n.icons {\r\n    text-align: center;\r\n}\r\n\r\n/* Menu */\r\n.verbar {\r\n    color: black;\r\n    background-color: #EDF2F5;\r\n}\r\n\r\n.submenu    {\r\n    position: absolute;\r\n    left: 0;\r\n    top: 0;\r\n    visibility: hidden;\r\n}\r\n\r\n.subframe   {\r\n    position: relative;\r\n    display: block;\r\n    background-color: #EDF2F5;\r\n    border: 1px solid #64879d;\r\n}\r\n\r\n.item   {\r\n    padding: 3px;\r\n    text-align: left;\r\n    white-space: nowrap;\r\n}\r\n\r\n.separator  {\r\n    background-color: #ff0000;\r\n    border-top: 1px solid #9e9a91;\r\n    border-bottom: 1px solid #ffffff;\r\n    margin: 2px 1px 2px 1px;    /* top right bottom left */\r\n    height: 0;\r\n    font-size: 1px;\r\n    line-height: 0\r\n}\r\n\r\n.item a {\r\n    position: relative;\r\n    display: block;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:link    {\r\n    color: #000000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:visited {\r\n    color: #000000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:hover   {\r\n    color: #ffffff;\r\n    background-color: #4b6983;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:active  {\r\n    color: #ff0000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item .fwdarr   {\r\n    position: absolute;\r\n    top: 5px;\r\n    right: 7px;\r\n}\r\n', 1, '2004-10-03 22:37:51', '2004-10-03 22:37:51' );
INSERT INTO {DB_PREFIX}templates ( TEMPLATE_ID, TEMPLATE_NAME, TEMPLATE_CONTENT, STYLESHEET, ACTIVE, CREATE_DATE, MODIFIED_DATE ) VALUES ( 3, 'Alternate', '<html>\r\n<head>\r\n<title>{title}</title>\r\n{stylesheet}\r\n</head>\r\n\r\n<body>\r\n\r\n<div class=\"menu\">\r\n{dhtmlmenu}\r\n</div>\r\n\r\n<div class=\"content\">\r\n{content}\r\n</div>\r\n\r\n</body>\r\n\r\n</html>', 'body {\r\n	margin: 0px 0px 0px 0px;\r\n	padding: 0px 0px 0px 0px;\r\n	font-family: verdana, arial, helvetica, sans-serif;\r\n	color: #ccc;\r\n	background-color: #333;\r\n	}\r\na {\r\n	text-decoration: none;\r\n	font-weight: bold;\r\n	color:  #ccc;\r\n	outline: none;\r\n	}\r\na:visited {\r\n	color:  #ccc;\r\n	}\r\na:active {\r\n	color:  #ccc;\r\n	}\r\na:hover {\r\n	color: #ccc;\r\n	text-decoration: underline;\r\n	}\r\n.ahem {\r\n	display: none;\r\n	}\r\nstrong, b {\r\n\r\n	font-weight: bold;\r\n	}\r\np {\r\n	font-size: 12px;\r\n	line-height: 22px;\r\n	margin-top: 20px;\r\n	margin-bottom: 10px; \r\n	}\r\n\r\n/* weird ie5win bug: all line-height to font-size ratios must agree or box gets pushed around. UPDATE: this has turned out to be very rare. my current recommendation is IGNORE this warning. at the moment i\'m leaving it in only in case the issue turns up again. possibly the original bug in march 2001 was caused by an unusual combination of factors, although this solved it at the time.*/\r\n\r\nh1 {\r\n	font-size: 24px;\r\n	line-height: 44px;\r\n	font-weight: bold;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n	}\r\nh2 {\r\n	font-size: 18px;\r\n	line-height: 40px;\r\n	font-weight: bold;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n	}\r\nh3 {\r\n	font-size: 16px;\r\n	line-height: 22px;\r\n	font-weight: bold;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n	}\r\nh4 {\r\n	font-size: 14px;\r\n	line-height: 26px;\r\n	font-weight: bold;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n	}\r\nh5 {\r\n	font-size: 12px;\r\n	line-height: 22px;\r\n	font-weight: bold;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n	}\r\nh6 {\r\n	font-size: 10px;\r\n	line-height: 18px;\r\n	font-weight: bold;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n	}\r\np.sectionname {\r\n	font-size: 14px;\r\n	line-height: 22px;\r\n	font-weight: bold;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n	}\r\nli {\r\n	font-size: 12px;\r\n	line-height: 22px;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n	}\r\nimg {\r\n	border: 0;\r\n	}\r\n.nowrap {\r\n	white-space: nowrap;\r\n	font-size: 10px;\r\n	font-weight: bold;\r\n	margin-top: 0;\r\n	margin-bottom: 0;\r\n/* must be combined with nobr in html for ie5win */\r\n	}\r\n.tiny {\r\n	font-size: 9px;\r\n	line-height: 16px;\r\n	margin-top: 15px;\r\n	margin-bottom: 5px; \r\n	}\r\ndiv.menu {\r\n	float: left;\r\n	padding: 10px 2% 10px 2%;\r\n	margin: 20px 1% 20px 2%;\r\n	background: #666;\r\n	position: fixed;\r\n	border: 5px solid #ccc;\r\n	width: 18%;\r\n	}\r\ndiv.content {\r\n	float: right;\r\n	padding: 10px 2% 10px 2%;\r\n	margin: 20px 2% 20px 1%;\r\n	background: #666;\r\n	border: 5px solid #ccc;\r\n	width: 68%;\r\n	/* height: 90%; */\r\n	/* overflow: scroll; */\r\n	}\r\npre {\r\n	font-size: 12px;\r\n	line-height: 22px;\r\n	margin-top: 20px;\r\n	margin-bottom: 10px; \r\n	}\r\ndiv.heading {\r\n    background-color: #64879d; \r\n    color: #EDF2F5;\r\n    padding: 9px; \r\n    text-align: left; \r\n    font-size: 16px; \r\n    font-weight: bold;  \r\n    text-transform: uppercase; \r\n}\r\ndiv.thebody { \r\n    padding: 5px; \r\n    margin-left: 150px; \r\n    font-size: 10pt; \r\n    font-family: Tahoma; \r\n}\r\np.smalltitle { \r\n    font-size: 12pt; \r\n    font-weight: bold; \r\n    margin: 0; \r\n    padding: 4px; \r\n    background-color: #CCCCCC; \r\n    text-transform: uppercase; \r\n    color: #64879d;\r\n}\r\n\r\n/*  All the menu css */\r\n.verbar {\r\n    color: black;\r\n    background-color: #64879d;\r\n}\r\n\r\n.submenu    {\r\n    position: fixed;\r\n    /* position: absolute; */\r\n    left: 0;\r\n    top: 0;\r\n    visibility: hidden;\r\n}\r\n\r\n.subframe   {\r\n    position: relative;\r\n    display: block;\r\n    background-color: #EDF2F5;\r\n    border: 1px solid #64879d;\r\n}\r\n\r\n.item   {\r\n    padding: 3px;\r\n    text-align: left;\r\n    white-space: nowrap;\r\n}\r\n\r\n.separator  {\r\n    background-color: #ff0000;\r\n    border-top: 1px solid #9e9a91;\r\n    border-bottom: 1px solid #ffffff;\r\n    margin: 2px 1px 2px 1px;    /* top right bottom left */\r\n    height: 0;\r\n    font-size: 1px;\r\n    line-height: 0\r\n}\r\n\r\n.item a {\r\n    position: relative;\r\n    display: block;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:link    {\r\n    color: #000000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:visited {\r\n    color: #000000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item a:hover   {\r\n    color: #ffffff;\r\n    background-color: #4b6983;\r\n    text-decoration: none;\r\n}\r\n.item a:active  {\r\n    color: #ff0000;\r\n    text-decoration: none;\r\n}\r\n\r\n.item .fwdarr   {\r\n    position: absolute;\r\n    top: 5px;\r\n    right: 7px;\r\n}\r\n', 1, '2004-10-03 22:37:51', '2004-10-03 22:37:51' );
